package com.github.xarmada.utilityset;import java.io.BufferedReader;import java.io.File;import java.io.FileNotFoundException;import java.io.FileReader;import java.io.IOException;import java.io.PrintWriter;import java.util.ArrayList;public class ConfigFile {public File file;public ConfigFile(String filename, String path) throws IOException {String[] pathSplit = path.split("/");File lastFile = null;for(String s: pathSplit) {File f;if(lastFile == null) {f = new File(s);} else {f = new File(lastFile, s);}if(!f.exists()) {f.mkdir();}lastFile = f;}file = new File(lastFile, filename);if(!file.exists()) {file.createNewFile();}}public void writeLines(ArrayList<String> lines) throws FileNotFoundException {PrintWriter pw = new PrintWriter(file);for(String s: lines) {pw.println(s);}pw.close();}public String readFirstLine() throws IOException {FileReader fileR = new FileReader(file);BufferedReader br = new BufferedReader(fileR);String line = br.readLine();br.close();return line;}public ArrayList<String> readAllLines() throws IOException {ArrayList<String> lines = new ArrayList<String>();FileReader fileR = new FileReader(file);BufferedReader br = new BufferedReader(fileR);String currentLine = br.readLine();lines.add(currentLine);while(currentLine != null) {currentLine = br.readLine();lines.add(currentLine);}br.close();return lines;}}