package com.github.xarmada.utilityset;public class ProxyVal {public String host;public String port;public ProxyVal(String host, String port) {this.host = host;this.port = port;}public static ProxyVal getProxyFromString(String s) {try {String[] split = s.split(":") ;ProxyVal answer = new ProxyVal(split[0], split[1]);return answer;} catch(Throwable ex) {ex.printStackTrace();}return null;}}